parameters.jsCode:
// Extract from nested body if it exists, otherwise use direct payload
const payload = $input.all()[0].json;
const data = payload.body || payload;

// Extract job information
const jobId = data.job_id || 'unknown';
const status = data.status || 'unknown';
const resultsCount = data.results_count || data.products_scraped || 0;
const message = data.message || '';
const errorMessage = data.error || '';

// Create status-specific content
let subject, html;

if (status === 'completed' && !errorMessage) {
  subject = `‚úÖ Kruidvat Scrape Completed Successfully (${resultsCount} products)`;
  html = `
    <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
      <div style="background: linear-gradient(135deg, #28a745, #20c997); color: white; padding: 20px; border-radius: 8px 8px 0 0;">
        <h1 style="margin: 0; font-size: 24px;">üõí Kruidvat Scrape Completed</h1>
      </div>
      <div style="background: #f8f9fa; padding: 20px; border-radius: 0 0 8px 8px; border: 1px solid #dee2e6;">
        <h2 style="color: #28a745; margin-top: 0;">‚úÖ Success!</h2>
        <p><strong>Products Scraped:</strong> <span style="color: #28a745; font-size: 18px; font-weight: bold;">${resultsCount}</span></p>
        <p><strong>Job ID:</strong> <code style="background: #e9ecef; padding: 2px 6px; border-radius: 4px;">${jobId}</code></p>
        <p><strong>Status:</strong> <span style="color: #28a745; font-weight: bold; text-transform: uppercase;">${status}</span></p>
        ${message ? `<p><strong>Message:</strong> ${message}</p>` : ''}
        <p><strong>Store:</strong> Kruidvat</p>
        <p><strong>Completed at:</strong> ${new Date().toLocaleString('nl-NL')}</p>
      </div>
    </div>
  `;
} else if (status === 'failed' || errorMessage) {
  subject = `‚ùå Kruidvat Scrape Failed (Job: ${jobId})`;
  html = `
    <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
      <div style="background: linear-gradient(135deg, #dc3545, #fd7e14); color: white; padding: 20px; border-radius: 8px 8px 0 0;">
        <h1 style="margin: 0; font-size: 24px;">üõí Kruidvat Scrape Failed</h1>
      </div>
      <div style="background: #f8f9fa; padding: 20px; border-radius: 0 0 8px 8px; border: 1px solid #dee2e6;">
        <h2 style="color: #dc3545; margin-top: 0;">‚ùå Failed</h2>
        <p><strong>Job ID:</strong> <code style="background: #e9ecef; padding: 2px 6px; border-radius: 4px;">${jobId}</code></p>
        <p><strong>Status:</strong> <span style="color: #dc3545; font-weight: bold; text-transform: uppercase;">${status}</span></p>
        ${message ? `<p><strong>Message:</strong> ${message}</p>` : ''}
        ${errorMessage ? `<div style="background: #f8d7da; border: 1px solid #f5c6cb; color: #721c24; padding: 10px; border-radius: 4px; margin: 10px 0;"><strong>Error:</strong> ${errorMessage}</div>` : ''}
        <p><strong>Products Scraped:</strong> ${resultsCount}</p>
        <p><strong>Store:</strong> Kruidvat</p>
        <p><strong>Failed at:</strong> ${new Date().toLocaleString('nl-NL')}</p>
      </div>
    </div>
  `;
} else if (status === 'cancelled' || status === 'stopped') {
  subject = `‚èπÔ∏è Kruidvat Scrape Stopped (Job: ${jobId})`;
  html = `
    <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
      <div style="background: linear-gradient(135deg, #6c757d, #adb5bd); color: white; padding: 20px; border-radius: 8px 8px 0 0;">
        <h1 style="margin: 0; font-size: 24px;">üõí Kruidvat Scrape Stopped</h1>
      </div>
      <div style="background: #f8f9fa; padding: 20px; border-radius: 0 0 8px 8px; border: 1px solid #dee2e6;">
        <h2 style="color: #6c757d; margin-top: 0;">‚èπÔ∏è Stopped</h2>
        <p><strong>Job ID:</strong> <code style="background: #e9ecef; padding: 2px 6px; border-radius: 4px;">${jobId}</code></p>
        <p><strong>Status:</strong> <span style="color: #6c757d; font-weight: bold; text-transform: uppercase;">${status}</span></p>
        ${message ? `<p><strong>Message:</strong> ${message}</p>` : ''}
        <p><strong>Products Scraped:</strong> ${resultsCount}</p>
        <p><strong>Store:</strong> Kruidvat</p>
        <p><strong>Stopped at:</strong> ${new Date().toLocaleString('nl-NL')}</p>
      </div>
    </div>
  `;
} else {
  subject = `‚ÑπÔ∏è Kruidvat Scrape Status Update (Job: ${jobId})`;
  html = `
    <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
      <div style="background: linear-gradient(135deg, #17a2b8, #6f42c1); color: white; padding: 20px; border-radius: 8px 8px 0 0;">
        <h1 style="margin: 0; font-size: 24px;">üõí Kruidvat Scrape Update</h1>
      </div>
      <div style="background: #f8f9fa; padding: 20px; border-radius: 0 0 8px 8px; border: 1px solid #dee2e6;">
        <h2 style="color: #17a2b8; margin-top: 0;">‚ÑπÔ∏è Status Update</h2>
        <p><strong>Job ID:</strong> <code style="background: #e9ecef; padding: 2px 6px; border-radius: 4px;">${jobId}</code></p>
        <p><strong>Status:</strong> <span style="color: #17a2b8; font-weight: bold; text-transform: uppercase;">${status}</span></p>
        ${message ? `<p><strong>Message:</strong> ${message}</p>` : ''}
        <p><strong>Products Scraped:</strong> ${resultsCount}</p>
        <p><strong>Store:</strong> Kruidvat</p>
        <p><strong>Updated at:</strong> ${new Date().toLocaleString('nl-NL')}</p>
      </div>
    </div>
  `;
}

return { subject, html };
